import React from 'react';
import { Card, CardContent, Typography, Grid } from '@material-ui/core';

import styles from './Cards.module.css'
import CountUp from 'react-countup';  //for making counter Animate
import cx from 'classnames' // for applying multiple classes 

const Cards = ({ data: { confirmed, recovered, deaths, lastUpdate } }) => {



    if (!confirmed) {
        return 'Loading';
    }

    return (

        <div className={styles.container}>
            <Grid container spacing={3} justify="center">

                <Grid item compponent={Card} xs={12} md={3} className={cx(styles.card, styles.infected)}>
                    <CardContent>
                        <Typography color="textSecondary" gutterBottom>Infected</Typography>
                        <Typography variant="h5">


                            <CountUp
                                start={0}
                                end={confirmed.value}
                                duration={2.5}
                                separator=","
                            />
                        </Typography>
    <Typography colr="textSecondary">{new Date (lastUpdate).toDateString()}</Typography>
                        <Typography variatnt="body2"> Number of Active Casses of Covid19</Typography>
                    </CardContent>
                </Grid>

                <Grid item compponent={Card} xs={12} md={3} className={cx(styles.card, styles.infected)}>
                    <CardContent>
                        <Typography color="textSecondary" gutterBottom>Recovered</Typography>
                        <Typography variant="h5">


                            <CountUp
                                start={0}
                                end={recovered.value}
                                duration={2.5}
                                separator=","
                            />
                        </Typography>
                        <Typography colr="textSecondary">{new Date (lastUpdate).toDateString()}</Typography>
                        <Typography variatnt="body2"> Number of Recovery Casses of Covid19</Typography>
                    </CardContent>
                </Grid>

                <Grid item compponent={Card}>
                    <CardContent>
                        <Typography color="textSecondary" gutterBottom>Deaths</Typography>
                        <Typography variant="h5">


                            <CountUp
                                start={0}
                                end={deaths.value}
                                duration={2.5}
                                separator=","
                            />
                        </Typography>
                        <Typography colr="textSecondary">{new Date (lastUpdate).toDateString()}</Typography>
                        <Typography variatnt="body2"> Number of Deaths Caused by Covid19</Typography>
                    </CardContent>
                </Grid>

            </Grid>


        </div>

    )


}

export default Cards;
